### ü¶Ä **`src-tauri/src/main.rs`**
```rust
#![cfg_attr(
  all(not(debug_assertions), target_os = "windows"),
  windows_subsystem = "windows"
)]

mod commands;
mod backend;

use axum::{routing::get, Router};
use std::net::SocketAddr;
use tokio::net::TcpListener;
use tower_http::cors::CorsLayer;
use tracing_subscriber;

#[tokio::main]
async fn main() {
  // Initialize logging
  tracing_subscriber::fmt::init();

  // Start Axum backend
  let api_port = std::env::var("TAURI_API_PORT")
    .unwrap_or("3001".to_string())
    .parse::<u16>()
    .expect("Invalid API port");

  let app = Router::new()
    .route("/health", get(|| async { "OK" }))
    .route("/ai/generate", get(backend::ai::generate))
    .layer(CorsLayer::permissive());

  let addr = SocketAddr::from(([127, 0, 0, 1], api_port));
  let listener = TcpListener::bind(addr).await.unwrap();

  println!("Backend API running on http://{}", addr);

  // Start Tauri
  let tauri_handle = tauri::Builder::default()
    .invoke_handler(tauri::generate_handler![
      commands::read_file,
      commands::write_file,
      commands::open_url
    ])
    .setup(|app| {
      let window = app.get_webview_window("main").unwrap();
      window.set_fullscreen(false).unwrap();
      Ok(())
    })
    .build(tauri::generate_context!())
    .expect("error while building tauri application");

  // Run both concurrently
  tokio::select! {
    _ = tauri_handle.run_until_stopped() => {},
    _ = axum::serve(listener, app) => {},
  }
}
```

