import React from 'react';

type TabType = 'local' | 'proxy' | 'web' | 'mobile';

interface TabsBarProps {
  activeTabs: TabType[];
  onTabToggle: (tab: TabType) => void;
}

export default function TabsBar({ activeTabs, onTabToggle }: TabsBarProps) {
  const tabs: { id: TabType; label: string }[] = [
    { id: 'local', label: 'Local Desktop' },
    { id: 'proxy', label: 'Proxy Network' },
    { id: 'web', label: 'Standard Web' },
    { id: 'mobile', label: 'Mobile App' },
  ];

  return (
    <div className="flex border-b border-gray-200 dark:border-gray-700 bg-white dark:bg-gray-900">
      {tabs.map((tab) => (
        <button
          key={tab.id}
          onClick={() => onTabToggle(tab.id)}
          className={`px-4 py-2 font-sans text-sm font-medium ${
            activeTabs.includes(tab.id)
              ? 'text-blue-600 border-b-2 border-blue-600 dark:text-blue-400'
              : 'text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200'
          }`}
        >
          {tab.label}
        </button>
      ))}
    </div>
  );
}
```

