import React, { useState } from 'react';
import { useJeanAI } from '../hooks/useJeanAI';

export default function JeanAssistant() {
  const [isOpen, setIsOpen] = useState(false);
  const { messages, sendMessage, isLoading } = useJeanAI();

  return (
    <>
      {/* Floating Jean Icon */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed top-4 right-4 z-50 w-12 h-12 rounded-full bg-gradient-to-r from-purple-500 to-indigo-600 shadow-lg hover:scale-110 transition-transform"
        aria-label="Open Jean Assistant"
      >
        <span className="text-white font-bold">J</span>
      </button>

      {/* Slide-out Panel */}
      {isOpen && (
        <div className="fixed top-0 right-0 h-full w-1/4 bg-white dark:bg-gray-800 shadow-xl z-40 flex flex-col">
          <div className="p-4 border-b border-gray-200 dark:border-gray-700 flex justify-between items-center">
            <h3 className="font-sans font-bold text-gray-800 dark:text-white">Jean Assistant</h3>
            <button onClick={() => setIsOpen(false)} className="text-gray-500 hover:text-gray-700">‚úï</button>
          </div>
          <div className="flex-1 overflow-y-auto p-4 space-y-3">
            {messages.map((msg, i) => (
              <div key={i} className={`p-2 rounded ${msg.role === 'user' ? 'bg-blue-50 dark:bg-blue-900/30' : 'bg-gray-100 dark:bg-gray-700'}`}>
                {msg.content}
              </div>
            ))}
            {isLoading && <div className="text-gray-500">Thinking...</div>}
          </div>
          <div className="p-4 border-t border-gray-200 dark:border-gray-700">
            <input
              type="text"
              placeholder="Ask Jean anything..."
              onKeyDown={(e) => e.key === 'Enter' && sendMessage(e.currentTarget.value)}
              className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded font-sans bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
            />
          </div>
        </div>
      )}
    </>
  );
}
```

