### üß† **React Hook: `src/hooks/useJeanAI.ts`**
```tsx
import { useState, useEffect } from 'react';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

export function useJeanAI() {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (input: string) => {
    if (!input.trim()) return;

    const newUserMsg: Message = { role: 'user', content: input };
    setMessages(prev => [...prev, newUserMsg]);
    setIsLoading(true);

    try {
      // Call local Axum backend (which may proxy to Llama.cpp or cloud)
      const res = await fetch('http://localhost:3001/ai/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: input, model: 'llama-3-70b' })
      });
      const data = await res.json();

      setMessages(prev => [...prev, { role: 'assistant', content: data.response }]);
    } catch (err) {
      console.error('AI Error:', err);
      setMessages(prev => [...prev, { role: 'assistant', content: 'Error connecting to AI.' }]);
    } finally {
      setIsLoading(false);
    }
  };

  return { messages, sendMessage, isLoading };
}
```

