### ‚û§ `src/components/BrowserView.tsx`
```tsx
import React from 'react';
import LocalDesktop from './LocalDesktop';
import ProxyNetwork from './ProxyNetwork';
import WebBrowser from './WebBrowser';
import MobileEmulator from './MobileEmulator';

type TabType = 'local' | 'proxy' | 'web' | 'mobile';

interface BrowserViewProps {
  activeTabs: TabType[];
  currentUrl: string;
  onSelectUrl: (url: string) => void;
}

export default function BrowserView({ activeTabs, currentUrl, onSelectUrl }: BrowserViewProps) {
  if (activeTabs.length === 0) {
    return (
      <div className="flex items-center justify-center h-full text-gray-500 dark:text-gray-400">
        Select a tab to begin
      </div>
    );
  }

  return (
    <div className="h-full overflow-hidden">
      {activeTabs.map((tab, index) => (
        <div key={tab} className="h-full">
          {tab === 'local' && <LocalDesktop />}
          {tab === 'proxy' && <ProxyNetwork />}
          {tab === 'web' && <WebBrowser url={currentUrl} onSelectUrl={onSelectUrl} />}
          {tab === 'mobile' && <MobileEmulator url={currentUrl} onSelectUrl={onSelectUrl} />}
        </div>
      ))}
    </div>
  );
}
```

