```rust
use tokio_postgres::{NoTls, Client, Config};
use std::sync::Arc;
use tokio::sync::Mutex;

#[derive(Clone)]
pub struct Database {
    client: Arc<Mutex<Client>>,
}

impl Database {
    pub async fn connect() -> Result<Self, Box<dyn std::error::Error>> {
        let config = Config::from_str(&format!(
            "host={} user={} password={} dbname={}",
            std::env::var("DB_HOST").unwrap_or("localhost".to_string()),
            std::env::var("DB_USER").unwrap_or("jeantrail".to_string()),
            std::env::var("DB_PASS").unwrap_or("secure_password_123".to_string()),
            std::env::var("DB_NAME").unwrap_or("jeantrail_dev".to_string()),
        ))?;

        let (client, connection) = config.connect(NoTls).await?;

        tokio::spawn(async move {
            if let Err(e) = connection.await {
                eprintln!("Database connection error: {}", e);
            }
        });

        Ok(Database {
            client: Arc::new(Mutex::new(client)),
        })
    }

    pub async fn get_client(&self) -> tokio_postgres::Result<Client> {
        Ok(self.client.lock().await.clone())
    }
}
