use axum::{Json, http::StatusCode};
use serde::{Deserialize, Serialize};
use stripe::{
    Client, CreateCheckoutSession, CreateCustomer, Customer, CustomerId,
    CheckoutSession, CreateBillingPortalSession, BillingPortalSession
};

#[derive(Deserialize)]
struct CheckoutRequest {
    price_id: String,
    user_id: String,
}

#[derive(Serialize)]
struct CheckoutResponse {
    id: String,
}

pub async fn create_checkout(
    Json(payload): Json<CheckoutRequest>,
) -> Result<Json<CheckoutResponse>, StatusCode> {
    let secret = std::env::var("STRIPE_SECRET_KEY").map_err(|_| StatusCode::INTERNAL_SERVER_ERROR)?;
    let client = Client::new(secret);

    // Get or create customer
    let customer_id = get_or_create_customer(&client, &payload.user_id).await
        .map_err(|_| StatusCode::INTERNAL_SERVER_ERROR)?;

    let mut params = CreateCheckoutSession::new();
    params.mode = Some(stripe::CheckoutSessionMode::Subscription);
    params.line_items = Some(vec![stripe::CreateCheckoutSessionLineItems {
        price: Some(payload.price_id),
        quantity: Some(1),
        ..Default::default()
    }]);
    params.customer = Some(customer_id);
    params.success_url = Some("http://localhost:1420/success");
    params.cancel_url = Some("http://localhost:1420/cancel");

    let session = CheckoutSession::create(&client, params)
        .await
        .map_err(|_| StatusCode::INTERNAL_SERVER_ERROR)?;

    Ok(Json(CheckoutResponse { id: session.id.to_string() }))
}

async fn get_or_create_customer(client: &Client, user_id: &str) -> Result<CustomerId, stripe::StripeError> {
    // In real app: look up customer by user_id in DB
    let mut create_params = CreateCustomer::new();
    create_params.metadata = Some(std::collections::HashMap::from([
        ("user_id".to_string(), user_id.to_string()),
    ]));
    let customer: Customer = Customer::create(client, create_params).await?;
    Ok(customer.id)
}

pub async fn stripe_webhook() -> StatusCode {
    // Handle subscription events
    StatusCode::OK
}
