## 1. ALIBABA SCRAPER

### ‚û§ `src-tauri/services/scraper/alibaba.rs`
```rust
use serde::{Deserialize, Serialize};
use reqwest;
use scraper::{Html, Selector};

#[derive(Serialize, Deserialize, Debug)]
pub struct Product {
    pub id: String,
    pub title: String,
    pub price: f64,
    pub min_order: u32,
    pub image_url: String,
    pub supplier: String,
    pub rating: f32,
    pub reviews: u32,
}

pub async fn scrape_alibaba(query: &str, page: u32) -> Result<Vec<Product>, Box<dyn std::error::Error>> {
    let url = format!(
        "https://www.alibaba.com/trade/search?fsb=y&IndexArea=product_en&CatId=&SearchText={}&page={}",
        urlencoding::encode(query),
        page
    );

    let client = reqwest::Client::new();
    let res = client
        .get(&url)
        .header("User-Agent", "Mozilla/5.0 (compatible; JeanTrailBot/1.0)")
        .send()
        .await?;

    let body = res.text().await?;
    let document = Html::parse_document(&body);

    let mut products = Vec::new();
    let product_selector = Selector::parse(".item-content").unwrap();

    for product in document.select(&product_selector).take(20) {
        if let Ok(prod) = parse_product(product) {
            products.push(prod);
        }
    }

    Ok(products)
}

fn parse_product(product: scraper::ElementRef<'_>) -> Result<Product, Box<dyn std::error::Error>> {
    let title = product
        .select(&Selector::parse("h2").unwrap())
        .next()
        .map(|t| t.inner_html())
        .unwrap_or_default();

    let price_text = product
        .select(&Selector::parse(".price").unwrap())
        .next()
        .map(|p| p.inner_html())
        .unwrap_or_default();

    let price = price_text
        .replace("$", "")
        .replace(",", "")
        .parse::<f64>()
