      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left: Product Image */}
        <div className="space-y-4">
          <div className="relative">
            <img
              src={currentProduct.imageUrl}
              alt={currentProduct.title}
              className="w-full h-96 object-cover rounded-lg border border-gray-200 dark:border-gray-700"
            />

            {/* NEW badge for new products */}
            {currentProduct.isNew && (
              <div className="absolute top-4 left-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-bold">
                NEW - 50% OFF
              </div>
            )}

            {/* Promo code badge */}
            {currentProduct.promoCode && (
              <div className="absolute top-4 right-4 bg-purple-500 text-white px-3 py-1 rounded-full text-sm font-bold">
                {currentProduct.promoCode}
              </div>
            )}
          </div>

          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <h3 className="font-semibold text-gray-800 dark:text-white">Supplier</h3>
            <p className="text-gray-600 dark:text-gray-300">{currentProduct.supplier}</p>
          </div>
        </div>

        {/* Right: Product Details */}
        <div className="space-y-6">
          <div>
            <h3 className="text-xl font-bold text-gray-800 dark:text-white mb-2">
              {currentProduct.title}
            </h3>
            <p className="text-gray-600 dark:text-gray-300 text-sm">
              {currentProduct.description.substring(0, 200)}...
