### ‚û§ `src/App.tsx`
```tsx
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import TabsBar from './components/TabsBar';
import BrowserView from './components/BrowserView';
import JeanAssistant from './components/JeanAssistant';
import { useJeanAI } from './hooks/useJeanAI';

type TabType = 'local' | 'proxy' | 'web' | 'mobile';

function App() {
  const [activeTabs, setActiveTabs] = useState<TabType[]>(['web']);
  const [currentUrl, setCurrentUrl] = useState('https://jeantrail.com');
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [isJeanOpen, setIsJeanOpen] = useState(false);
  const { messages, sendMessage, isLoading } = useJeanAI();

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as 'light' | 'dark' | null;
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      const systemPrefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setTheme(systemPrefersDark ? 'dark' : 'light');
    }
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const handleTabToggle = (tab: TabType) => {
    setActiveTabs((prev) => {
      if (prev.includes(tab)) {
        return prev.filter((t) => t !== tab);
      } else {
        return [...prev, tab];
      }
    });
  };

  const handleSelectUrl = (url: string) => {
    setCurrentUrl(url);
    if (!activeTabs.includes('web')) {
      setActiveTabs((prev) => [...prev, 'web']);
    }
  };

  return (
    <div className={`h-screen flex flex-col font-sans ${theme === 'dark' ? 'dark bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'}`}>
      <Header
        theme={theme}
        toggleTheme={toggleTheme}
        openJean={() => setIsJeanOpen(true)}
        user={{ name: 'Demo User', email: 'user@example.com' }}
      />

      <TabsBar activeTabs={activeTabs} onTabToggle={handleTabToggle} />

      <div className="flex-1 overflow-hidden">
        <SplitViewManager>
          <BrowserView
            activeTabs={activeTabs}
            currentUrl={currentUrl}
            onSelectUrl={handleSelectUrl}
          />
        </SplitViewManager>
      </div>

      <JeanAssistant
        isOpen={isJeanOpen}
        onClose={() => setIsJeanOpen(false)}
        messages={messages}
        onSend={sendMessage}
        isLoading={isLoading}
      />
    </div>
  );
}

export default App;
```

