## üß† **1. LLAMA.CPP INTEGRATION**

### ‚û§ `src-tauri/llama/model.rs`
```rust
use std::sync::Arc;
use tokio::sync::RwLock;
use llama_cpp::{Llama, LlamaParams};

#[derive(Clone)]
pub struct LlamaModel {
    model: Arc<RwLock<Option<Llama>>>,
}

impl LlamaModel {
    pub fn new() -> Self {
        Self {
            model: Arc::new(RwLock::new(None)),
        }
    }

    pub async fn load(&self, model_path: &str) -> Result<(), Box<dyn std::error::Error>> {
        let params = LlamaParams::default()
            .with_n_ctx(4096)
            .with_n_threads(8)
            .with_n_gpu_layers(20); // Auto-fallback if no GPU

        let llama = Llama::load_from_file(model_path, params)?;
        let mut model = self.model.write().await;
        *model = Some(llama);
        Ok(())
    }

    pub async fn is_loaded(&self) -> bool {
        self.model.read().await.is_some()
    }

    pub async fn inference(&self, prompt: &str, max_tokens: u32) -> Result<String, Box<dyn std::error::Error>> {
        let model = self.model.read().await;
        if let Some(llama) = &*model {
            let output = llama.inference(
                prompt,
                max_tokens,
                0.95,   // temperature
                40,     // top_k
                0.9,    // top_p
                false,  // stream (Phase 1: non-streaming first)
            )?;
            Ok(output)
        } else {
            Err("Model not loaded".into())
        }
    }
}
