```ts
import { google } from 'googleapis';

const SCOPES = ['https://www.googleapis.com/auth/drive.file'];
const CLIENT_ID = import.meta.env.VITE_GOOGLE_DRIVE_CLIENT_ID;

export class DriveService {
  private token: string | null = null;

  async authenticate() {
    return new Promise<string>((resolve, reject) => {
      const authUrl = `https://accounts.google.com/o/oauth2/v2/auth?client_id=${CLIENT_ID}&redirect_uri=${encodeURIComponent(window.location.origin + '/auth/google')}&scope=${SCOPES.join(' ')}&response_type=token&state=jeantrail`;

      const popup = window.open(authUrl, 'GoogleAuth', 'width=500,height=600');

      const checkMessage = (event: MessageEvent) => {
        if (event.origin !== window.location.origin) return;
        if (event.data.type === 'GOOGLE_AUTH_SUCCESS') {
          window.removeEventListener('message', checkMessage);
          popup?.close();
          this.token = event.data.token;
          resolve(event.data.token);
        } else if (event.data.type === 'GOOGLE_AUTH_ERROR') {
          window.removeEventListener('message', checkMessage);
          popup?.close();
          reject(new Error('Authentication failed'));
        }
      };

      window.addEventListener('message', checkMessage);
    });
  }

  async uploadFile(file: File, parentId?: string): Promise<string> {
    if (!this.token) throw new Error('Not authenticated');

    const metadata = {
      name: file.name,
      mimeType: file.type,
      parents: parentId ? [parentId] : undefined,
    };

    const form = new FormData();
    form.append('metadata', new Blob([JSON.stringify(metadata)], { type: 'application/json' }));
    form.append('file', file);

    const response = await fetch('https://www.googleapis.com/upload/drive/v3/files?uploadType=multipart', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${this.token}`,
      },
      body: form,
    });

    if (!response.ok) throw new Error('Upload failed');
    const data = await response.json();
    return data.id;
  }

  async downloadFile(fileId: string): Promise<Blob> {
    if (!this.token) throw new Error('Not authenticated');

    const response = await fetch(`https://www.googleapis.com/drive/v3/files/${fileId}?alt=media`, {
      headers: { Authorization: `Bearer ${this.token}` },
    });

    if (!response.ok) throw new Error('Download failed');
    return await response.blob();
  }
}
