use serde::{Deserialize, Serialize};
use std::process::Command;

#[derive(Deserialize)]
struct VideoRequest {
    prompt: String,
    duration: u32, // seconds (5-120)
    style: String, // "photorealistic", "anime", etc.
}

#[derive(Serialize)]
struct VideoResponse {
    video_url: String,
    job_id: String,
}

pub async fn generate_video(req: VideoRequest) -> Result<VideoResponse, Box<dyn std::error::Error>> {
    let job_id = uuid::Uuid::new_v4().to_string();
    let output_path = format!("/tmp/{}.mp4", job_id);

    // Call CogVideoX via CLI or Python subprocess
    let output = Command::new("python3")
        .args(&[
            "cogvideo/generate.py",
            "--prompt", &req.prompt,
            "--duration", &req.duration.to_string(),
            "--output", &output_path,
            "--style", &req.style,
        ])
        .output()
        .await?;

    if !output.status.success() {
        return Err("Video generation failed".into());
    }

    // Upload to S3 or return local path
    Ok(VideoResponse {
        video_url: format!("/videos/{}.mp4", job_id),
        job_id,
    })
}
