pub async fn process_payment(
    amount: f64,
    currency: &str,
    method: PaymentMethod,
    user_id: &str
) -> Result<String, PaymentError> {
    match method {
        PaymentMethod::Stripe => stripe::charge(amount, currency, user_id).await,
        PaymentMethod::PayPal => paypal::process(amount, currency, user_id).await,
        PaymentMethod::Crypto(coin) => crypto::send(amount, &coin, user_id).await,
        PaymentMethod::Local(region) => local_payments::process(amount, currency, &region, user_id).await,
    }
}
