## üìÑ **1. `scrape_products_v3.js`**  
*Enhanced Alibaba scraper with upload date, taxonomy, pricing intelligence*

```javascript
// src/services/scraper/scrape_products_v3.js
const axios = require('axios');
const cheerio = require('cheerio');
const fs = require('fs').promises;
const path = require('path');
const categories = require('./categories.json');

// Load configuration
const config = require('./scraper-config.json');

class AlibabaScraper {
  constructor() {
    this.session = axios.create({
      timeout: 10000,
      headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36 JeanTrailBot/1.0',
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.5',
        'Accept-Encoding': 'gzip, deflate',
        'Connection': 'keep-alive',
        'Upgrade-Insecure-Requests': '1',
      }
    });
  }

  // Extract upload date from Alibaba product page
  extractUploadDate($) {
    try {
      // Multiple possible selectors for upload date
      const dateSelectors = [
        '.detail-desc-declar p:contains("Upload")',
        '.product-detail span:contains("Added")',
        '.product-info-item:contains("Listed")',
        '[data-spm="d_1_1_1_3"]'
      ];

      for (const selector of dateSelectors) {
        const dateText = $(selector).text();
        if (dateText) {
          // Extract date pattern (e.g., "Added on Jan 15, 2024")
          const dateMatch = dateText.match(/(\d{1,2}\/\d{1,2}\/\d{4}|\w+ \d{1,2}, \d{4}|\d{4}-\d{2}-\d{2})/);
          if (dateMatch) {
            return new Date(dateMatch[0]);
          }
        }
      }

      // Fallback: use current date if not found
      return new Date();
    } catch (error) {
      console.warn('Failed to extract upload date:', error.message);
      return new Date();
    }
  }

  // Auto-categorize product using taxonomy
  autoCategorize(title, description) {
    const text = (title + ' ' + description).toLowerCase();

    for (const [category, keywords] of Object.entries(categories)) {
      for (const keyword of keywords) {
        if (text.includes(keyword.toLowerCase())) {
          return category;
        }
      }
    }

    return 'Other';
  }

  // Generate promo code for new products
  generatePromoCode() {
    const randomPart = Math.random().toString(36).substring(2, 7).toUpperCase();
    return `JEANTRAIL-NEW-${randomPart}`;
  }

  // Extract Alibaba price (handle various formats)
  extractAlibabaPrice($) {
    try {
      const priceSelectors = [
        '.price-current',
        '.ma-reference-price',
        '.product-price-value',
        '[data-spm="d_1_1_1_1"] .price',
        '.price em'
      ];

      for (const selector of priceSelectors) {
        let priceText = $(selector).text().trim();
        if (priceText) {
          // Clean price text (remove currency symbols, commas, etc.)
          priceText = priceText.replace(/[^\d.-]/g, '');
          const price = parseFloat(priceText);
          if (!isNaN(price) && price > 0) {
            return price;
          }
        }
      }

      return 0;
    } catch (error) {
      console.warn('Failed to extract Alibaba price:', error.message);
      return 0;
    }
  }

  // Main scraping function
  async scrapeProduct(url) {
    try {
      const response = await this.session.get(url);
      const $ = cheerio.load(response.data);

      // Extract basic info
      const title = $('.module-pdp-title h1').text().trim() || 
                   $('h1.product-title').text().trim() ||
                   $('.detail-title a').text().trim() ||
                   'Unknown Product';

      const description = $('.product-description').text().trim() ||
                         $('.detail-desc-declar').text().trim() ||
                         '';

      const imageUrl = $('.main-image img').attr('src') ||
                      $('.gallery-img img').attr('src') ||
                      'https://via.placeholder.com/300';

      const supplier = $('.company-name').text().trim() ||
                      $('.supplier-name').text().trim() ||
                      'Unknown Supplier';

      const minOrder = parseInt($('.min-order').text().replace(/\D/g, '')) || 1;

      // Enhanced extraction
      const uploadDate = this.extractUploadDate($);
      const alibabaPrice = this.extractAlibabaPrice($);
      const category = this.autoCategorize(title, description);

      // Check if new (<24 hours)
      const isNew = (new Date() - uploadDate) < (24 * 60 * 60 * 1000);
      const promoCode = isNew ? this.generatePromoCode() : null;
      const promotionEndDate = isNew ? new Date(Date.now() + 24 * 60 * 60 * 1000) : null;

      // Create product object
      const product = {
        id: `alibaba_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        source: 'alibaba',
        title,
        description,
        imageUrl,
        supplier,
        minOrder,
        uploadDate: uploadDate.toISOString(),
        alibabaPrice,
        category,
        isNew,
        promoCode,
        promotionEndDate: promotionEndDate ? promotionEndDate.toISOString() : null,
        status: 'pending_review',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };

      return product;
    } catch (error) {
      console.error(`Failed to scrape ${url}:`, error.message);
      throw new Error(`Scraping failed for ${url}: ${error.message}`);
    }
  }

  // Batch scrape multiple products
  async scrapeBatch(urls) {
    const products = [];
    const errors = [];

    for (let i = 0; i < urls.length; i++) {
      try {
        console.log(`Scraping ${i + 1}/${urls.length}: ${urls[i]}`);
        const product = await this.scrapeProduct(urls[i]);
        products.push(product);

        // Add delay to avoid rate limiting
        await new Promise(resolve => setTimeout(resolve, config.scraping.delayMs));
      } catch (error) {
        errors.push({ url: urls[i], error: error.message });
        console.error(`Error on ${urls[i]}:`, error.message);
      }
    }

    return { products, errors };
  }

  // Save products to file
  async saveProducts(products, filepath) {
    try {
      const dir = path.dirname(filepath);
      await fs.mkdir(dir, { recursive: true });

      const existing = await this.loadExistingProducts(filepath);
      const allProducts = [...existing, ...products];

      // Remove duplicates by URL or ID
      const uniqueProducts = allProducts.filter((product, index, self) =>
        index === self.findIndex(p => p.id === product.id)
      );

      await fs.writeFile(filepath, JSON.stringify(uniqueProducts, null, 2));
      console.log(`Saved ${uniqueProducts.length} products to ${filepath}`);
    } catch (error) {
      console.error('Failed to save products:', error.message);
      throw error;
    }
  }

  // Load existing products
  async loadExistingProducts(filepath) {
    try {
      await fs.access(filepath);
      const data = await fs.readFile(filepath, 'utf8');
      return JSON.parse(data);
    } catch (error) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }
}

module.exports = AlibabaScraper;

// CLI interface
if (require.main === module) {
  (async () => {
    const args = process.argv.slice(2);
    if (args.length === 0) {
      console.log('Usage: node scrape_products_v3.js <url1> <url2> ...');
      process.exit(1);
    }

    const scraper = new AlibabaScraper();
    const { products, errors } = await scraper.scrapeBatch(args);

    if (products.length > 0) {
      const outputPath = path.join(__dirname, '../../..', 'src-tauri/data/products/new_products.json');
      await scraper.saveProducts(products, outputPath);
    }

    if (errors.length > 0) {
      console.log(`Errors: ${errors.length}/${args.length}`);
      errors.forEach(err => console.log(`- ${err.url}: ${err.error}`));
    }

    console.log(`Successfully scraped ${products.length} products`);
  })();
}
```

