```tsx
// src/services/scraper/admin-product-processor.tsx
import React, { useState, useEffect } from 'react';
import { Product, SmartPricingResult } from './types';
import { calculateSmartPricing } from './price-intelligence-service';

interface AdminProductProcessorProps {
  products: Product[];
  onApprove: (product: Product) => void;
  onReject: (productId: string) => void;
  onEditPrice: (product: Product, newPrice: number) => void;
}

export default function AdminProductProcessor({
  products,
  onApprove,
  onReject,
  onEditPrice
}: AdminProductProcessorProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [editedPrice, setEditedPrice] = useState<number | null>(null);
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [pricingResult, setPricingResult] = useState<SmartPricingResult | null>(null);

  const currentProduct = products[currentIndex];

  useEffect(() => {
    if (currentProduct) {
      // Calculate smart pricing when product changes
      const result = calculateSmartPricing({
        alibabaPrice: currentProduct.alibabaPrice,
        weight: 450, // Mock weight - in real app, this would come from product data
        hasFreeShipping: true,
        amazonPrice: 0, // These would be fetched from price intelligence service
        aliExpressPrice: 0
      });
      setPricingResult(result);
      setEditedPrice(result.finalPrice);
    }
  }, [currentProduct]);

  if (!currentProduct) {
    return (
      <div className="flex items-center justify-center h-64 text-gray-500">
        No products to review
      </div>
    );
  }

  const handleApprove = () => {
    if (editedPrice !== null) {
      const updatedProduct = { ...currentProduct, finalPrice: editedPrice };
      onApprove(updatedProduct);
    }
    moveToNext();
  };

  const handleReject = () => {
    onReject(currentProduct.id);
    moveToNext();
  };

  const moveToNext = () => {
    if (currentIndex < products.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      // Reset when done
      setCurrentIndex(0);
    }
  };

  const handleEditPrice = () => {
    if (editedPrice !== null) {
      onEditPrice(currentProduct, editedPrice);
    }
    setShowEditDialog(false);
  };

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Product Review Queue</h2>
        <p className="text-gray-600 dark:text-gray-300">
          Processed {currentIndex + 1}/{products.length} products
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left: Product Image */}
        <div className="space-y-4">
          <div className="relative">
            <img
              src={currentProduct.imageUrl}
              alt={currentProduct.title}
              className="w-full h-96 object-cover rounded-lg border border-gray-200 dark:border-gray-700"
            />

            {/* NEW badge for new products */}
            {currentProduct.isNew && (
              <div className="absolute top-4 left-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-bold">
                NEW - 50% OFF
              </div>
            )}

            {/* Promo code badge */}
            {currentProduct.promoCode && (
              <div className="absolute top-4 right-4 bg-purple-500 text-white px-3 py-1 rounded-full text-sm font-bold">
                {currentProduct.promoCode}
              </div>
            )}
          </div>

          <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
            <h3 className="font-semibold text-gray-800 dark:text-white">Supplier</h3>
            <p className="text-gray-600 dark:text-gray-300">{currentProduct.supplier}</p>
          </div>
        </div>

        {/* Right: Product Details */}
        <div className="space-y-6">
          <div>
            <h3 className="text-xl font-bold text-gray-800 dark:text-white mb-2">
              {currentProduct.title}
            </h3>
            <p className="text-gray-600 dark:text-gray-300 text-sm">
              {currentProduct.description.substring(0, 200)}...
            </p>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
              <h4 className="font-semibold text-blue-800 dark:text-blue-200">Category</h4>
              <p className="text-blue-700 dark:text-blue-300">{currentProduct.category}</p>
            </div>

            <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
              <h4 className="font-semibold text-green-800 dark:text-green-200">Min Order</h4>
              <p className="text-green-700 dark:text-green-300">{currentProduct.minOrder} units</p>
            </div>
          </div>

          <div className="bg-yellow-50 dark:bg-yellow-900/20 p-4 rounded-lg">
            <h4 className="font-semibold text-yellow-800 dark:text-yellow-200 mb-2">Pricing Intelligence</h4>
            {pricingResult && (
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Alibaba Cost:</span>
                  <span className="font-mono">${pricingResult.alibabaPrice.toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Smart Margin:</span>
                  <span className="font-mono">{pricingResult.marginPercentage}%</span>
                </div>
                <div className="flex justify-between font-bold text-lg">
                  <span>Final Price:</span>
                  <span className="font-mono">${pricingResult.finalPrice.toFixed(2)}</span>
                </div>
                <div className="text-sm text-yellow-700 dark:text-yellow-300">
                  {pricingResult.reasoning}
                </div>
              </div>
            )}
          </div>

          <div className="flex space-x-4">
            <button
              onClick={() => setShowEditDialog(true)}
              className="flex-1 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
            >
              Edit Price
            </button>

            <button
              onClick={handleReject}
              className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
            >
              Reject
            </button>

            <button
              onClick={handleApprove}
              className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
            >
              Approve
            </button>
          </div>
        </div>
      </div>

      {/* Edit Price Dialog */}
      {showEditDialog && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white dark:bg-gray-800 p-6 rounded-xl w-full max-w-md">
            <h3 className="text-lg font-bold mb-4">Edit Product Price</h3>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Final Price ($)
              </label>
              <input
                type="number"
                step="0.01"
                value={editedPrice || ''}
                onChange={(e) => setEditedPrice(parseFloat(e.target.value) || 0)}
                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              />
            </div>

            <div className="flex justify-end space-x-3">
              <button
                onClick={() => setShowEditDialog(false)}
                className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
              >
                Cancel
              </button>
              <button
                onClick={handleEditPrice}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                Save
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
