## üìÑ **3. `price-intelligence-service.ts`**  
*Smart pricing calculator with mock APIs*

```typescript
// src/services/scraper/price-intelligence-service.ts

export interface PricingInput {
  alibabaPrice: number;
  weight: number; // in grams
  hasFreeShipping: boolean;
  amazonPrice: number;
  aliExpressPrice: number;
}

export interface SmartPricingResult {
  alibabaPrice: number;
  finalPrice: number;
  marginPercentage: number;
  reasoning: string;
}

// Mock API functions (replace with real implementations)
export async function queryAmazonPrice(productTitle: string): Promise<number> {
  // In production, this would call Amazon Product Advertising API
  // For now, return a mock price based on Alibaba price
  const basePrice = Math.max(1.2, Math.random() * 2 + 1) * 10;
  return Math.round(basePrice * 100) / 100;
}

export async function queryAliExpressPrice(productTitle: string): Promise<number> {
  // In production, this would scrape AliExpress or use their API
  const basePrice = Math.max(1.1, Math.random() * 1.5 + 0.5) * 10;
  return Math.round(basePrice * 100) / 100;
}

// Smart pricing calculator
export function calculateSmartPricing(input: PricingInput): SmartPricingResult {
  let marginPercentage = 40; // Default margin
  let reasoning = 'Default 40% margin applied';

  // Check if Amazon has lower price
  if (input.amazonPrice > 0 && input.amazonPrice < input.alibabaPrice * 1.25) {
    marginPercentage = 25;
    reasoning = 'Reduced to 25% margin (Amazon competitive pricing)';
  }

  // Check for lightweight + free shipping bonus
  if (input.weight < 500 && input.hasFreeShipping) {
    marginPercentage += 10;
    reasoning += ' +10% margin (lightweight with free shipping)';
  }

  // Calculate final price
  const finalPrice = input.alibabaPrice * (1 + marginPercentage / 100);

  return {
    alibabaPrice: input.alibabaPrice,
    finalPrice: Math.round(finalPrice * 100) / 100,
    marginPercentage,
    reasoning
  };
}

// Batch pricing calculator
export async function calculateBatchPricing(products: Array<{
  alibabaPrice: number;
  title: string;
}>, weight: number = 450, hasFreeShipping: boolean = true): Promise<SmartPricingResult[]> {
  const results: SmartPricingResult[] = [];

  for (const product of products) {
    // Mock API calls
    const amazonPrice = await queryAmazonPrice(product.title);
    const aliExpressPrice = await queryAliExpressPrice(product.title);

    const input: PricingInput = {
      alibabaPrice: product.alibabaPrice,
      weight,
      hasFreeShipping,
      amazonPrice,
      aliExpressPrice
    };

    results.push(calculateSmartPricing(input));
  }

  return results;
}
